SELECT DISTINCT(cm.COURSE_ID) AS "FULL COURSE ID",
	(SUBSTRING(cm.course_id,7,10)) AS "SHORT COURSE ID",
    cm.COURSE_NAME AS "COURSE NAME",
    COUNT(u.STUDENT_ID) AS "NUMBER OF STUDENTS"
FROM course_main cm
LEFT OUTER JOIN course_users cu on cu.crsmain_pk1 = cm.pk1
LEFT OUTER JOIN users u on cu.users_pk1 = u.pk1
LEFT OUTER JOIN (SELECT cm2.COURSE_ID AS courseID, STRING_AGG(u2.STUDENT_ID, ', ' ORDER BY u2.STUDENT_ID)
    FROM course_main cm2
    INNER JOIN course_users cu2 on cu2.crsmain_pk1 = cm2.pk1
    INNER JOIN users u2 on cu2.users_pk1 = u2.pk1
    AND cu2.ROLE IN ('S')
    AND cu2.AVAILABLE_IND='Y'
    AND cm2.ROW_STATUS = 0
    GROUP BY cm2.COURSE_ID, cm2.COURSE_NAME,cm2.START_DATE
